    private void readData() {
        txtFlightName.setText(data.getName());
        txtCode.setText(data.getCode());
        cbSource.setSelectedItem(data.getSource());
        cbDestination.setSelectedItem(data.getDestination());
        txtDate.setText(data.getDate().toString());
    }

