    private void btnSearchActionPerformed(java.awt.event.ActionEvent evt) {// GEN-FIRST:event_btnSearchActionPerformed
        try {
            var winFlights = new FlightListView();
            winFlights.setVisible(true);
            dispose();
        } catch (IOException ex) {
            Logger.getLogger(ReservationView.class.getName()).log(Level.SEVERE, null, ex);
        }
    }// GEN-LAST:event_btnSearchActionPerformed

    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {
        var mapPassenger = _utility.getDataComponents(pnPassenger);
        mapPassenger.put("idFlight", data.getIdFlight());
        mapPassenger.put("address", txtAddress.getText());

        var mapPayment = _utility.getDataComponents(pnPayment);
        System.out.println(mapPassenger);

        try {
            passenger = (Passenger) _utility.intanceObject(Passenger.class, mapPassenger);
            System.out.println(passenger);
            _passengerController.create(passenger);

            passenger = _passengerController.find(x -> x.getPassport() == passenger.getPassport()
                    && x.getEmail().equalsIgnoreCase(passenger.getEmail())
                    && x.getIdFlight() == passenger.getIdFlight());

            System.out.println(passenger);
            payment = (Payment) _utility.intanceObject(Payment.class, mapPayment);
            System.out.println(payment);
            payment.setIdPassenger(passenger.getIdFlight());
            _paymentController.create(payment);

        } catch (NoSuchMethodException | InstantiationException | InvocationTargetException | IllegalAccessException
                | IllegalArgumentException | IntrospectionException e) {

            e.printStackTrace();
        }
    }// GEN-LAST:event_btnSaveActionPerformed

